import {useState} from 'react'
export const CheckoutForm = ( {onConfirm}) => {
    const [nombre, setNombre] = useState('')
    const [telefono, setTelefono] = useState('')
    const [email, setEmail] = useState('')

    const handleConfirm = (event) => {
        event.preventDefault()

        const datosUsuario = {
            nombre, telefono, email
        }
        onConfirm(datosUsuario)
    }

    return (
        <div className='container'>
            <form onSubmit={handleConfirm}>
                <label>
                    Nombre
                    <input type='text' name='nombre' value={nombre} onChange={( { target}) => setNombre(target.value)} className='form form-control' />
                </label>
                <label>
                    Tel√©fono
                    <input type='text' name='telefono' value={telefono} onChange={( { target}) => setTelefono(target.value)} className='form form-control'  />
                </label>
                <label>
                    E-Mail
                    <input type='text' name='email' value={email} onChange={( { target}) => setEmail(target.value)} className='form form-control'  />
                </label>
                <label>
                    Enviar pedido
                    <input type='submit' value='Enviar'/>
                </label>
            </form>

        </div>
    )
}
