import { CheckoutForm } from '../CheckoutForm/CheckoutForm'
import { db } from '../../db/firebase'
import { useState, useContext } from 'react'
import { CartContext } from "../../context/CartContext"
import { Timestamp, doc, setDoc, collection } from 'firebase/firestore'

export const Checkout = () => {
    const [cargando, setCargando] = useState(false)
    const [numeroOrden, setNumeroOrden] = useState('')
    const { cart, total, limpiarCarrito } = useContext(CartContext)
    const createOrder = async ({ nombre, telefono, email }) => {
        setCargando(true)
        console.log("Estoy!")

        const objPedido = {
            buyer: {
                nombre, telefono, email
            },
            items: cart,
            total: total,
            date: Timestamp.fromDate(new Date())
        }
        //const pedidoRef = doc(db, 'orders');
        //setDoc(pedidoRef, objPedido)
        //await setDoc(doc(db, 'orders', 'pepe'), objPedido)
        //setNumeroOrden(pedidoRef.id)
        db.collection("orders").add(objPedido)
            .then(function (docRef) {
                setCargando(false)
                setNumeroOrden(docRef.id)
            })
            .catch(function (error) {
                console.log("ERROR: " + error)
            })
        }



if (cargando) {
    return <h1>Generando orden...</h1>
}
if (numeroOrden) {
    return <h1>Tu ID de orden es {numeroOrden}</h1>
}

return (
    <div>
        <h1>Checkout</h1>
        <CheckoutForm onConfirm={createOrder} />
    </div>
)
}
